$.exports.range = function(m, n) {
    return Math.floor(Math.random() * (m - n + 1) + n);
}
$.exports.mapColType = function(target, type, n) {
    for (let i = 0; i < n; i++) {
        target.push({
            col_type: type
        });
    }
}
$.exports.getApi = function(k) {
    return api.host + api[k];
}
$.exports.localRuleVer = function(localRuleList, rule) {
    let localRule;
    for (let i = 0; i < localRuleList.length; i++) {
        let item = localRuleList[i];
        let au = item.author || "";
        if (item.title === rule.name && au.length > 0 && rule.author.includes(au)) {
            localRule = item;
            break;
        }
    }
    if (localRule === void 0) {
        return -1;
    } else {
        return localRule.version;
    }
}
