function lazyParse(playUrl,ms,tm){
    let tm = parseInt(tm)||3000;
    //log("è®¿é—®è¶…æ—¶:"+tm);
    log("å°è¯•é“é•¿ä»“åº“x5å…å—…:"+playUrl);
    clearVar("_x5jxUrl");
    let refer=playUrl.split("//")[0]+"//"+playUrl.split("//")[1].split("/")[0];
    let bad=$("ç½‘ç»œä¸ä½³æˆ–è€…ç–‘ä¼¼å¯¹æ–¹ç½‘ç«™æŒ‚äº†ï¼Œä½ è¦æŸ¥çœ‹å…¶ç½‘é¡µå—ï¼Ÿ").confirm((playUrl)=>{
             log("ç½‘ç«™çœ‹èµ·æ¥æŒ‚äº†:"+playUrl);
            return playUrl
        },playUrl);
    try{
        var back=request(playUrl,{headers: {"Referer": refer},timeout:tm});
        if(!back){
            return bad
        }
    }catch(e){
        log(e.message);
        return bad
    }
    try {
         back=JSON.parse(back);
         if(!back.url){
             log(back);
         }
        return back.url
    }catch (e) {
        // log("éjsoné”™è¯¯:"+e.message);
        let ms = parseInt(ms)||5000;
        //log("ms:"+ms);
        //log("ms/250:"+(ms/250));
        let max_count=(ms/250);
        showLoading('å—…æ¢ä¸­ï¼Œè‹¥å¤±è´¥å°±é‡è£…x5...');
        let _x5 = $.toString((playUrl,max_count) => {
            if(typeof(request)=='undefined'||!request){
                eval(fba.getInternalJs());
            }

            fba.putVar("_x5jxUrl",playUrl);
            //let _x5jxUrl = playUrl;
            if(window.count == null){
                window.count=1;
            }
            window.count++;
            //fba.log('count:'+window.count+',max_count:'+max_count);
            if( window.count >= max_count){
                fba.log("è¶…è¿‡"+max_count*250+"æ¯«ç§’æœªè·å–åˆ°èµ„æºåœ°å€ï¼Œè·³åˆ°æºç½‘é¡µ");
                return fba.getVar("_x5jxUrl");
                //return _x5jxUrl
            }
            function muteMe(elem) {
                try {
                    elem.pause();
                }
                catch (e) {
                }
            }
            var videos = document.querySelectorAll("video"), audios = document.querySelectorAll("audio"), v2 = document.querySelectorAll("embed"), v3 = document.querySelectorAll("#player");
            try {
                [].forEach.call(videos, function (video) {
                    muteMe(video);
                });
            } catch (e) {}
            try {
                [].forEach.call(audios, function (audio) {
                    muteMe(audio);
                });
            } catch (e) {}
            try {
                [].forEach.call(v2, function (v) {
                    muteMe(v);
                });
            } catch (e) {}
            try {
                [].forEach.call(v3, function (v) {
                    muteMe(v);
                });
            } catch (e) {}
            var urls = _getUrls();
            let exceptWords = '.js$|.css$|.ts$|.html$|.htm$|.php$|.gif$|.jpg$|.jpeg$|.png$|.ico$|.svg$|.txt$'.split('|').map(it=>'\\'+it).join('|');
            var exceptKeys = new RegExp(exceptWords);
            let replaceWords = 'playm3u8|m3u8\.tv'.split('|').map(it=>it).join('|');
            var replaceKeys = new RegExp(replaceWords,'g');
            let videoWords = ".mp4$|.m3u8$|.flv$|.avi$|.3gp$|.mpeg$|.wmv$|.mov$|rmvb|.dat$|qqBFdownload|mime=video%2F|mime_type=video_".split('|').map((it)=>{
                if(it.startsWith(".")){
                    return '\\'+it
                }else{
                    return it
                }
            }).join("|");
            var videoKeys = new RegExp(videoWords);
            for (var i in urls) {
                if ((videoKeys.test(urls[i])||videoKeys.test(urls[i].replace(replaceKeys,"").split("?")[0]) )&& !exceptKeys.test(urls[i].split("?")[0])) {
                    return urls[i]+"#isVideo=true#";
                }
            }
        },playUrl,max_count);
        let x5Url = 'x5Rule://'+playUrl+ '@' +_x5;
        return x5Url
    }
}
