String.prototype.strip = function () { return this.replace(/(^\s*)|(\s*$)/g, ""); };
function task_req(task_api,task) { // é€šç”¨å‘é€ä»“åº“postè°ƒç”¨ä»»åŠ¡è¯·æ±‚
    const {getFile} = $.require("hiker://page/utiliy?rule=é“é•¿ä»“åº“Pro");
    let houseFile = getFile||false;
    if(!houseFile){
        setItem('is_house_vip', 'false');
    }
    let house_data = houseFile();
    let result = request(task_api, {
        headers: {
            'content-type': 'application/json'
        },
        body: JSON.stringify({
            "params": {
                "data": {
                    "type": task.mode||'run',
                    "func": task.func||'',
                    "params": task.params||[]
                },
                "auth": {
                    "name": house_data.username||"",
                    "password": house_data.password||""
                }
            }
        }),
        method: 'POST'
    });
    return JSON.parse(result).result;
}

function task_upload(task){ // é€šç”¨ä»»åŠ¡å‘å¸ƒæ–°å¢åŠä¿®æ”¹åŠŸèƒ½
    const {api,getFile} = $.require("hiker://page/utiliy?rule=é“é•¿ä»“åº“Pro");
    let houseFile = getFile||false;
    if(!houseFile){
        setItem('is_house_vip', 'false');
    }
    let api_url = api.host+'/hikerule/task/add';
    let house_data = houseFile();
    let result = request(api_url, {
        headers: {
            'content-type': 'application/json'
        },
        body: JSON.stringify({
            "params": {
                "data": {
                    "name": task.name,
                    "pycode": task.pycode,
                    "note": task.note,
                    "test_code": task.test_code,
                },
                "auth": {
                    "name": house_data.username||"",
                    "password": house_data.password||""
                }
            }
        }),
        method: 'POST'
    });
    return JSON.parse(result).result;
}

function task_call(id,func,params){
    const {api} = $.require("hiker://page/utiliy?rule=é“é•¿ä»“åº“Pro");
    let task_api = api.host+'/hikerule/task/run/'+id;
    let task = {
        mode:'call',
        func:func,
        params:params||[]
    };
    return task_req(task_api,task);
}

function task_rename(id,new_task_name){
    const {api,getFile} = $.require("hiker://page/utiliy?rule=é“é•¿ä»“åº“Pro");
    let houseFile = getFile||false;
    if(!houseFile){
        setItem('is_house_vip', 'false');
    }
    let house_data = houseFile();
    let task_api = api.host+'/hikerule/task/rename/'+id;
    task_api = task_api+'?name='+house_data.username+'&password='+house_data.password+'&task_name='+new_task_name;
    try {
        let result = JSON.parse(fetch(task_api,{}));
        if(result.status==0){
            return  result.detail
        }else{
            return result.result+''+result.detail
        }
    }catch (e) {
        log(e.message);
        return ''+e.message
    }
}

