$.exports = {
    runCode: function(rule) {log(rule)
        try {
            let [input, code] = rule.split('.js:')
            return code ? eval(code) : rule
        } catch (e) { return rule }
    },
    urlParse: function(url, pageNum, func) {
        url = url.split(';').map((v) => v.replace(/ï¼›ï¼›/g, ';'))
        if (func) func(url)
        url[0] = url[0].replace(/fypage(?:@(-?\d+)@)?(?:\*(\d+)@)?/, (_, start, space) => parseInt(start || 0) + 1 + (pageNum - 1) * parseInt(space || 1))
        url[0] = /^(.*?)(?:\[firstPage=(.*?)\])?$/.exec(url[0])
        url[0] = runCode(url[0][2] && pageNum == 1 ? url[0][2] : url[0][1])
        let options = { headers: { 'Content-Type': 'text/plain; charst=UTF-8' }, method: url[1] }
        // postæ–¹æ³•æ—¶è½¬æ¢å‚æ•°
        if (/^post$/i.test(options['method'])) {
            let [oriUrl, body] = url[0].split('?')
            url[0] = oriUrl.replace(/ï¼Ÿï¼Ÿ/g, '?')
            if (body.startsWith('JsonBody=')) body = body.slice(9)
            options['body'] = body
        }
        if (url[2]) options.headers['Content-Type'] = options.headers['Content-Type'].slice(0, 19) + url[2]
        if (url[3]) url[3].match(/{(.*)}/)[1].split('&&').forEach((v) => {
            let [key, value] = v.split('@')
            options.headers[key] = runCode(value)
        })
        // æ·»åŠ å…¨å±€UA
        if (!options.headers['User-Agent']) {
            if (MY_RULE.ua == 'pc')
                options.headers['User-Agent'] = PC_UA
            else if (MY_RULE.ua == 'mobile')
                options.headers['User-Agent'] = MOBILE_UA
        }
        return { MY_URL: url[0], indexHtml: fetch(url[0], options) }
    }
}

