/*
var lazy = `@lazyRule=.js:var url = input;if(url.match(/youku|mgtv|ixigua|qq.com|qiyi|migu|bili|sohu|pptv|letv|le/)){eval("var config =" + fetch("hiker://files/cache/MyParseSet.json"));eval(fetch(config.cj));aytmParse(url)}else{url}`;
*/

function lazy(lists, i, j) {
    let url = lists[i][j].url
    if (i != 0) {
        return url + `@lazyRule=.js:` + $.toString(() => {
            var url = input;
            if (url.match(/youku|mgtv|ixigua|qq.com|qiyi|migu|bili|sohu|pptv|letv|le/)) {
                return request('https://jhjx.ptygx.com/?url=' + url).match(/url":"(.*?)"/)[1]
            } else {
                return url
            }
        })
    }
    let ol = lists.filter(it => {
        let u = it[j].url
        return !(u.match(/youku|mgtv|ixigua|qq.com|qiyi|migu|bili|sohu|pptv|letv|le/))
    }).map(it => it[j].url)
    if (url.match(/youku|mgtv|ixigua|qq.com|qiyi|migu|bili|sohu|pptv|letv|le/)) {
        return url + `@lazyRule=.js:` + $.toString((others) => {
            var url = input;
            let u = request('https://jhjx.ptygx.com/?url=' + url).match(/url":"(.*?)"/)[1]
            return JSON.stringify({
                urls: [u].concat(others)
            })
        }, ol)
    }
    return JSON.stringify({
        urls: ol
    })
}


