function ClassTab(classArray, params) {
    Object.assign(this, params)
    this.arr = classArray.map(v => this.init(v))
    this.sign = '$' + MY_RULE.title + '_' + (this.name || '') + '_'
    this.color = this.color || '#12b668'
    this.boundary = this.boundary || 'blank_block'
}
ClassTab.prototype = {
    constructor: ClassTab,
    load(el) {
        let folded = getVar('fold_' + this.sign, '')
        if (this.fold) el.push({
            title: 'â€œâ€œâ€â€<span style="color:#049eff">' + (folded ? 'â–¶' : 'â–¼'),
            url: $('#noLoading#').lazyRule((sign, folded) => {
                putVar('fold_' + sign, { '': 'T', 'T': '' } [folded])
                refreshPage(false)
                return 'hiker://empty'
            }, this.sign, folded),
            col_type: 'scroll_button'
        })
        let arr = folded ? [this.arr[0]] : this.arr
        arr.forEach((v) => {
            let { id, class_name, class_url } = v,
                selected = JSON.stringify(this.getClass(id))
            class_name.forEach((name, i) => {
                let url = class_url[i],
                    now = JSON.stringify({ name: name, url: url })
                el.push({
                    title: (selected == now ? 'â€œâ€œâ€â€<span style="color:' + this.color + '"><b>' : '') + name,
                    url: $('#noLoading#').lazyRule((sign, id, now) => {
                        putVar(sign + id, now)
                        putVar(sign, now)
                        refreshPage(false)
                        return 'hiker://empty'
                    }, this.sign, id, now),
                    col_type: 'scroll_button'
                })
            })
            el.push({ col_type: this.boundary })
        })
    },
    init(classObject) {
        if (typeof classObject.class_name == 'string')
            classObject.class_name = classObject.class_name.split('&')
        if (typeof classObject.class_url == 'string')
            classObject.class_url = classObject.class_url.split('&')
        return classObject
    },
    push(classObject) {
        this.arr.push(this.init(classObject))
    },
    getClass(id) {
        let defaultClass = this.arr.find(item => item.id == id)
        if (defaultClass) defaultClass = JSON.stringify({
            name: defaultClass.class_name[0],
            url: defaultClass.class_url[0]
        })
        else throw new Error('cannot find id: ' + id + ' in classTab: ' + this.sign)
        return JSON.parse(getVar(this.sign + id, defaultClass))
    },
    getLastClick() {
        return JSON.parse(getVar(this.sign, '{}'))
    },
    setUrl(url) {
        return url.replace(/\$\{([^}]*)\}/g, (_, id) => this.getClass(id).url)
    }
}
$.exports = ClassTab

