let myCollection_el = [],
    pageNum = parseInt(MY_URL.split('#')[1]),
    RULE = MY_PARAMS.RULE,
    fyAll = RULE.url.includes('fyAll'),
    // åŠ è½½ClassTabç»„ä»¶
    ClassTab = $.require('hiker://page/ClassTab?rule=' + getItem('myCollection')),
    tabHeader = []

if (RULE.class_name) tabHeader.push({
    id: fyAll ? 'fyAll' : 'fyclass',
    class_name: RULE.class_name,
    class_url: RULE.class_url
})
if (RULE.area_name) tabHeader.push({
    id: fyAll ? 'fyAll' : 'fyarea',
    class_name: RULE.area_name,
    class_url: RULE.area_url
})
if (RULE.year_name) tabHeader.push({
    id: fyAll ? 'fyAll' : 'fyyear',
    class_name: RULE.year_name,
    class_url: RULE.year_url
})
if (RULE.sort_name) tabHeader.push({
    id: fyAll ? 'fyAll' : 'fysort',
    class_name: RULE.sort_name,
    class_url: RULE.sort_url
})
tabHeader = new ClassTab(tabHeader, { name: RULE.title })
if (pageNum == 1) {
    addListener('onClose', 'clearVar("myCollection-searchMode")')
    putVar('myCollection-searchMode', RULE.title)
    tabHeader.load(myCollection_el)
    if (RULE.search_url) myCollection_el.push({
        title: "æœç´¢",
        desc: "æœä½ æƒ³è¦çš„...",
        url: '"hiker://page/singleSearch?keyword="+input+"&page=fypage"',
        extra: { rule: getItem('myCollection'), RULE: RULE },
        col_type: "input",
    })
}
// å¤„ç†MY_URLå’ŒMY_RULE
MY_RULE.ua = RULE.ua
MY_RULE.title = RULE.title
MY_RULE.col_type = RULE.col_type
MY_RULE.detail_col_type = RULE.detail_col_type
MY_RULE.find_rule = RULE.find_rule
MY_RULE.detail_find_rule = RULE.detail_find_rule
MY_RULE.preRule = RULE.preRule
MY_RULE.pageList = JSON.parse(RULE.pages || '[]')
MY_RULE.pages = JSON.stringify(MY_RULE.pageList)
MY_RULE.last_chapter_rule = RULE.last_chapter_rule
MY_RULE.params = {}

const { runCode, urlParse } = $.require('hiker://page/utility?rule=' + getItem('myCollection'))
var { MY_URL, MY_URL_Options } = urlParse(RULE.url, {}, pageNum, (url) => {
    url[0] = fyAll ? url[0].replace(/fyAll/g, '$${fyAll}') : url[0].replace(/fy(class|area|year|sort)/g, '$${fy$1}')
    url[0] = tabHeader.setUrl(url[0])
}),
    indexHtml = fetch(MY_URL, MY_URL_Options)
MY_RULE.url = MY_URL
MY_RULE.urlHeaders = MY_URL_Options.headers
// æ­£æ–‡è§£æ
if (pageNum == 1) eval(MY_RULE.preRule)
if (MY_RULE.find_rule.startsWith('js:')) {
    function getUrl() {
        return MY_URL
    }

    function getResCode() {
        return indexHtml
    }

    function setResult(el, param1, param2, param3) {
        param1 = CALLBACK_KEY
        param2 = MY_RULE
        param3 = MY_TYPE
        if (Array.isArray(el.data)) el = el.data
        Array.prototype.push.apply(myCollection_el, el.map((v) => {
            let MY__RULE = Object.assign({}, MY_RULE)
            v.col_type = v.col_type || MY_RULE.col_type

            if (!v.url) {
                return v
            } else if (v.url.includes('@rule=')) {
                let [_, url, rule] = v.url.match(/^([\s\S]*?)@rule=([\s\S]*)$/)
                v.url = url
                MY__RULE.detail_find_rule = rule
                MY__RULE.detail_col_type = MY_RULE.col_type
            } else if (v.url.startsWith('hiker://page/')) {
                if (v.url.includes('rule=') || (v.extra || {}).rule)
                    return v
                let [_, path, params] = v.url.split('#')[0].match(/^hiker:\/\/page\/(.+?)(?:\?(.*))?$/),
                    subPage = MY_RULE.pageList.find((v) => v.path == path),
                    subUrl = (params || '').split('&').find((v) => v.startsWith('url='))
