$.exports = {
    dataLoad: function(showAll) {
        //æ£€æŸ¥é—´éš”ï¼Œé»˜è®¤ä¸€å¤©
        let min = 60 * 24 * 1,
            data = [],
            time = parseInt(readFile('updatetime')) || 0,
            now = new Date().getTime()
        if (now - time > 1000 * 60 * min) {
            log('æ›´æ–°äº†æ•°æ®')
            // è·å–è¿œç¨‹æ•°æ®
            let url = getItem('remoteUrl')
            try {
                data = fetch(url)
                if (url.startsWith('hiker://page/'))
                    data = JSON.parse(data).rule
                data = JSON.parse(data)
            } catch (e) {
                data = []
            }
            data = data.filter((v) => { return v.title != getItem('myCollection') && !v.author.includes('è½»åˆé›†ç”Ÿæˆå™¨') })
            saveFile('dataCache', JSON.stringify(data))
            saveFile('updatetime', '' + now)
        } else {
            log('æ²¡æœ‰æ›´æ–°æ•°æ®')
            data = JSON.parse(readFile('dataCache'))
        }
        // å†™å…¥è‡ªå®šä¹‰æ•°æ®
        let customData = JSON.parse(readFile('customData') || '[]'),
            rewriteData = []
        customData = customData.reduce((self, v) => {
            let index = data.findIndex((vv) => v.title == vv.title)
            if (index >= 0) {
                self.push(v)
                let rule = data.splice(index, 1)[0]
                if(showAll || v.visible)
                    rewriteData.push(Object.assign(rule, v))
            }
            return self
        }, [])
        data.forEach((v) => customData.push({ title: v.title, visible: true }))
        saveFile('customData', JSON.stringify(customData))
        return rewriteData.concat(data)
    },
    runCode: function(rule) {
        try {
            let [input, code] = rule.split('.js:')
            return code ? eval(code) : rule
        } catch (e) { return rule }
    },
    urlParse: function(url, headers, pageNum, func) {
        url = url.split(';').map((v) => v.replace(/ï¼›ï¼›/g, ';'))
        if (func) func(url)
        url[0] = url[0].replace(/fypage(?:@(-?\d+)@)?(?:\*(\d+)@)?/, (_, start, space) => parseInt(start || 0) + 1 + (pageNum - 1) * parseInt(space || 1))
        url[0] = /^(.*?)(?:\[firstPage=(.*?)\])?$/.exec(url[0])
        url[0] = runCode(url[0][2] && pageNum == 1 ? url[0][2] : url[0][1])
        let options = { headers: headers, method: url[1] }
        // postæ–¹æ³•æ—¶è½¬æ¢å‚æ•°
        if (/^post$/i.test(options['method'])) {
            let [oriUrl, body] = url[0].split('?')
            url[0] = oriUrl.replace(/ï¼Ÿï¼Ÿ/g, '?')
            if (body.startsWith('JsonBody=')) body = body.slice(9)
            options['body'] = body
        }
        if (url[2]) options.headers['Content-Type'] = 'text/plain;charst=' + url[2]
        if (url[3]) url[3].match(/{(.*)}/)[1].split('&&').forEach((v) => {
            let [key, value] = v.split('@')
            options.headers[key] = runCode(value)
        })
        // æ·»åŠ å…¨å±€UA
        if (!options.headers['User-Agent']) {
            if (MY_RULE.ua == 'pc')
                options.headers['User-Agent'] = PC_UA
            else if (MY_RULE.ua == 'mobile')
                options.headers['User-Agent'] = MOBILE_UA
        }
        return { MY_URL: url[0], MY_URL_Options: options }
    }
}

