function split(text, target, count) {
    count = count === undefined ? 1 : count;
    let array = [text];
    for (let i = 0; i < count; i++) {
        text = array[i];
        let index = text.indexOf(target);
        if (index === -1) {
            break;
        }
        array[i] = text.slice(0, index);
        array[i + 1] = text.slice(index + target.length);
    }
    return array;
}

function rsplit(text, target, count) {
    count = count === undefined ? 1 : count;
    let array = [text];
    for (let i = 0; i < count; i++) {
        text = array[0];
        let index = text.lastIndexOf(target);
        if (index === -1) {
            break;
        }
        array[0] = text.slice(0, index);
        array.splice(1, 0, text.slice(index + target.length));
    }
    return array;
}
