const E2Error = $.require("hiker://page/E2Error?rule=E2ToHiker");
function getE2Rex(E2Hiker,t,r) {
    let arr = [];
    let funcName = "",
        funcParam = "";
    let status = 0;
    let tran = false;
    for (let i = 0; i < r.length; i++) {
        let k = r[i],
            s = i > 0 ? r[i - 1] : "",
            x = i + 1 < r.length ? r[i + 1] : "";
        if (k === "." && status === 0) {
            status = 1;
            continue;
        } else if (status === 1 && k !== "(") {
            funcName += k;
            continue;
        } else if (status === 1 && k === "(") {
            if (k + r[i + 1] + r[i + 2] === "(@@") {
                i++, i++;
                status = 3;
            } else {
                status = 2;
            }
            continue;
        } else if (status === 3 && k + r[i + 1] + r[i + 2] !== "@@)") {
            funcParam += k;
            continue;
        } else if (status === 3 && k + r[i + 1] + r[i + 2] === "@@)") {
            i++, i++;
            arr.push([funcName, funcParam]);
            status = 0, funcName = "", funcParam = "";
            continue;
        } else if (status === 2 && tran) {
            tran = false;
            funcParam += k;
            continue;
        } else if (status === 2 && k === "\\") {
            tran = true;
            funcParam += k;
            continue;
        } else if (status === 2 && k !== ")" && k !== "\\") {
            funcParam += k;
            continue;
        } else if (status === 2 && k === ")") {
            if (["z", "z2", "th", "fg"].includes(funcName)) {
                funcParam = funcParam.replace(/\\\)/g, ")").replace(/\\\(/g, "(");
            }
            arr.push([funcName, funcParam]);
            status = 0, funcName = "", funcParam = "";
            continue;
        } else {
            throw new E2Error("æœªå‘ç°å‡½æ•°èµ·å§‹ç¬¦[.]:)",r,t);
        }
    }
    if (status !== 0) {
        throw new E2Error("æ‰¾ä¸åˆ°ç»“æŸç¬¦[)]",r,t);
    }
    let e2Hiker = new E2Hiker(t);
    var target;
    try {
        for (target of arr) {
            let y = e2Hiker[target[0]](target[1]);
            if (target[0] === "or" && y === true) {
                break;
            }
        }
    } catch (e) {
        if(e.type==="dataType"){
            e.rule=target[0];
            throw e;
        }else if(/Cannot find function (.*?) /.test(e.message)){
            throw new E2Error("æœªçŸ¥çš„å‡½æ•°"+RegExp.$1,r,t);
        }else{
            throw e;
        }
    }
    return e2Hiker.valueOf();
}

$.exports = getE2Rex;

