js:
var res = {};
var d = [];
var html = getResCode();
var pics = [];

try {
    var list = parseDomForArray(html, '#read_tpc&&img');
    var title = parseDomForHtml(html, "h4&&Text");
    putVar('title', title);
        for (var key in list) {
            var pic = parseDomForHtml(list[key], 'img&&src')
            //log({pic:pic});
            pics.push(pic);
        }
    for (x in pics) {
        d.push({
            pic_url: pics[x],
            url: pics[x],
            col_type: "pic_3"
        });
    }
    putVar('pics', pics);
    d.unshift({
	  title:title + "("+pics.length+"p)",
	  col_type: 'text_center_1', extra: { lineVisible: false }
	});
    d.push({
        title: '下载全部图片',
        url: $(' 是否下载全部图片').confirm(_ => {
            var picsStr = getVar('pics')
            var titleStr = getVar('title');
            var jsonObj = JSON.parse(picsStr);//转换为json对象
            var pics = [];
            for (var i = 0; i < jsonObj.length; i++) {
                var s = jsonObj[i];
                var file = s.split(".");
                var len = file.length
                var suffix = file[len - 1];
                if (!fileExist('hiker://files/download/' + titleStr + '/' + (i + 1) + '.' + suffix)) {
                    saveImage(s, 'hiker://files/download/' + titleStr + '/' + (i + 1) + '.' + suffix);
                }
            }

            return 'toast://下载成功';
        }),
        col_type: 'text_center_1', extra: { lineVisible: false }
    });

} catch (error) {
    log({ error: error });
}
res.data = d;
setHomeResult(res);

