function requestData() {
    let html = fetch('https://elsbdz.com/', {});
    let list = pdfa(html,'body&&ul&&a'),
       success = false;
  let urls=[];
  for( let k in list){
	urls.push({
	  url:pdfh(list[k],'a&&href')
	})
  }
    urls.shift()
    urls.unshift(new Date().toISOString())
    saveFile('urls.json', JSON.stringify(urls), 0)
    success = true
    log('更新网址' + (success ? '成功' : '失败'))
    return success
}
let urls = readFile('urls.json', false)
if (urls) {   
    if (Date.now() - Date.parse(urls[0]) > 24*60*60*1000 )
        (new java.lang.Thread(function() {
            if (requestData()) {
                confirm({
                    title: '提示',
                    content: '更新地址成功'
                })
                refreshPage(false)
            } else {
                urls[0] = new Date().toISOString()
                saveFile('urls.json', JSON.stringify(urls), 0)
            }
        })).start()
} else if (!requestData()) {
    confirm({
        title: '提示',
        content: '获取数据失败，请刷新重试'
    })
}

