            clearItem('searchHistoryShowStatus')
        }))

        const page = MY_URL.split('$$')[1]

        if (parseInt(page) === 1) {
            picacg.d.push({
                title: '搜索',
                url: $.toString(() => {
                    const picacg = $.require('hiker://page/picacg')
                    picacg.setSearchHistory(input)
