    function parse(originUrls, num) {
        var a = originUrls.slice(0),
            b = originUrls.length,
            c = b - num,
            d, e;
        while (b-- > c) {
            e = Math.floor((b + 1) * Math.random());
            d = a[e];
            a[e] = a[b];
            a[b] = d
        };
        return a.slice(c);
    }
    var urls = parse(originUrls, 3);
    for (var i = 0; i < urls.length; i++) {
        var arrylist = [];
        for (var j = 4; j <= urls[i].length; j += 4) {
            arrylist.push(String.fromCharCode(parseInt(urls[i].toString().substr(j - 4, 4)) - 1000))
        };
        urls[i] = arrylist.join('');
    };

    return urls[Math.round(Math.random()*(3 - 1))] + '?_ts=' + Math.random();
}"}]

