function uprulefile() {
    try {
        var ruletxt = fetch('https://gitlab.com/timiking/HikerRules/-/raw/master/zywcj/zywcj.txt', {});
        var rulejs = fetch('https://gitlab.com/timiking/HikerRules/-/raw/master/zywcj/zywcj.js', {});
    } catch (e) {
        rulejs = "";
        ruletxt = "";
    }
    if (rulejs.search(/lazyRule/) != -1) {
        writeFile("hiker://files/rules/timik/zywcj.js", rulejs);
        writeFile("hiker://files/rules/timik/zywcj.txt", ruletxt);
    } else {
        setError('规则文件获取失败，可能网络有问题。')
    };
};

//检测是否有规则与JS文件
if (fileExist("hiker://files/rules/timik/zywcj.js") && fileExist("hiker://files/rules/timik/zywcj.txt")) {
    //执行预处理
    var lac = fetch('hiker://files/rules/timik/zywcj.js');
    if (lac.search(/lazyRule/) != -1) {
        eval(lac);
    } else {
        //如果本地文件不对将尝试自修复
        eval(fetch('hiker://files/rules/timik/zywcj.js'));
        zywpre();
    };
} else {
    uprulefile();
    //执行预处理
    eval(fetch('hiker://files/rules/timik/zywcj.js'));
    zywpre();
};

