    extra=extra||false;
    title=title||'网站触发了人机验证，点此处理';
    /*
    var url=$().lazyRule((title,MY_URL,id,set,extra,注入验证码识别)=>{
    if(getVar(set,'')&&!getMyVar('cookie')){
    putMyVar('cookie',getVar(set,''));
    refreshPage(true);
    return'hiker://empty';
    }
    return $(MY_URL).rule((注入验证码识别,id,set,extra)=>{
                //log(typeof(注入验证码识别))
                let d=[];
