var s_page = parseInt(getVar('s_page', '0'));
if (s_page != 0 && t_page > s_page) {
    t_page = s_page;
}
if (/home|meinv/.test(url)) {
    var t_url = url;
} else {
    var t_url = url.replace(/\d+.html/, t_page + '.html');
}
if (parseInt(getVar('page', '-1')) - MY_PAGE == -1) {
    putVar('t_page', (t_page + 1) + '');
}
putVar('page', MY_PAGE + '');
var t_url_tmp = getVar('t_url', t_url);
if (/home|meinv/.test(t_url_tmp)) {
    t_url = t_url_tmp.replace(/index-.+/, 'index.html');
} else {
    t_url = t_url_tmp;
}
clearVar('t_url');
log('t_url:' + t_url);
var html = fetch(t_url, {
    headers: {
        'User-Agent': PC_UA
    }
});
if (!/index/.test(t_url)) {
    var 翻页 = true;
    var _tmp = pdfh(html, 'body&&.pagination&&li,-1&&Text').match(/\d+/g);
    var s_page = _tmp[0];
    putVar('s_page', s_page);
} else {
    var 翻页 = false;
}
if (MY_PAGE == 1) {
    var list_c = pdfa(html, 'body&&#section-menu,0&&.row-item').slice(1, -1);
    for (var m in list_c) {
        var group = pdfa(list_c[m], 'body&&a');
        for (var n in group) {
            var title = Decrypt(pdfh(group[n], 'a&&title'), '883346').replace(/\"/g, '');
            d.push({
                title: n == 0 ? strong(title, 'ff9933') :
                    (m + '' + n) == getVar('g_i_mm', '0') ?
                    strong(title, '339966') : title,
                url: $((n == 0 ? pd(group[1], 'a&&href') : pd(group[n], 'a&&href')) + '#noLoading#').lazyRule((m, n) => {
                    putVar('g_u_mm', input.replace('-1.html', '-1.html'));
                    putVar('g_i_mm', m + '' + n);
                    refreshPage(false);
                    return 'hiker://empty';
                }, m, n),
                col_type: 'scroll_button',
            });
        }
        d.push({
            col_type: 'line'
        });
    }
}
if (翻页) {
    d.push({
        col_type: 'line',
    });
    d.push({
        title: strong('首页', 'ff9933'),
        url: $('#noLoading#').lazyRule(() => {
            putVar('t_page', '1');
            refreshPage(false);
            return 'hiker://empty';
        }),
        col_type: 'text_5',
    });
    d.push({
        title: strong('当前第' + t_url.match(/(\d+)\.html/)[1] + '页', 'ff9933'),
        url: 'hiker://empty',
        col_type: 'text_5',
    });
    d.push({
        title: strong('跳转页数', 'ff9933'),
        url: $('').input((t_url) => {
            input = input.match(/\d+/)[0];
            t_url = t_url.replace(/\d+\.html/, input + '.html');
            putVar('t_url', t_url);
            putVar('t_page', input);
            refreshPage(false);
        }, t_url),
        col_type: 'text_5',
    });
    d.push({
        title: strong('共' + s_page + '页', 'ff9933'),
        url: $(s_page + '#noLoading#').lazyRule(() => {
            putVar('t_page', input);
            refreshPage(false);
            return 'hiker://empty';
        }),
        col_type: 'text_5',
    });
    d.push({
        col_type: 'line',
    });
}
if (/shipin/.test(t_url)) {
