    for (var list of list) {
        d.push({
            title: Decrypt(pdfh(list, 'a&&title'), '883346').replace(/\"/g, ''),
            img: img_dn + pdfh(list, 'img&&data-original') + '@js=' + $.toString((ruleName) => {
                const {
                    imgDecrypt
                } = $.require("hiker://page/imgDES?rule=" + ruleName);
                return imgDecrypt();
            }, MY_RULE.title),
            url: $(pd(list, 'a&&href')).lazyRule(() => {
