        d.push({
            title: Decrypt(pdfh(list, 'a&&title'), '883346').replace(/\"/g, ''),
            img: img_dn + pdfh(list, 'img&&data-original') + '@js=' + $.toString((ruleName) => {
                const {
                    imgDecrypt
                } = $.require("hiker://page/imgDES?rule=" + ruleName);
                return imgDecrypt();
            }, MY_RULE.title),
            url: $(pd(list, 'a&&href')).lazyRule(() => {
                var html = fetch(input);
                var video = pdfh(html, 'body&&td.name&&a&&href');
                log(video);
                return video;
            }),
        });
    }
} else if (/tupian/.test(t_url)) {
    var list = pdfa(html, 'body&&.list-row&&ul&&li');
    for (var list of list) {
        d.push({
            title: Decrypt(pdfh(list, 'a&&title'), '883346').replace(/\"/g, ''),
            img: img_dn + pdfh(list, 'img&&data-original') + '@js=' + $.toString((ruleName) => {
                const {
                    imgDecrypt
                } = $.require("hiker://page/imgDES?rule=" + ruleName);
                return imgDecrypt();
            }, MY_RULE.title),
            url: $(pd(list, 'a&&href')).lazyRule(() => {
                var html = fetch(input);
                var pics = [];
                pdfa(html, 'body&&.img-row&&img').forEach(list => {
                    var img = 'https://mmimage.1vkx.cn/' + pdfh(list, 'img&&data-original') + '@js=' + $.toString((ruleName) => {
                        const {
                            imgDecrypt
                        } = $.require("hiker://page/imgDES?rule=" + ruleName);
