var res = {};
var d = [];
var html = getResCode();
var pics = [];
try {
    var list_rule = '';
    var pic_rule = ".entry&&img&&src";
    var title_rule = "h1&&Text";
    var num_rule = "#dm-fy&&a,-2&&Text";
    var next_url_rule = MY_URL + "?page={page}";
    var charset = "UTF-8";
    var user_agent = 'Windows';
    var num = parseDomForHtml(html, num_rule);
    //log({num:num});
    let htmlArr = [];
    htmlArr.push({ url: MY_URL, options: { headers: { 'User-Agent': user_agent,'content-type':'application/json; charset='+charset } } });
    for (i = 2; i <= num; i++) {
        var next_url = next_url_rule.replace("{page}",i);
        //log({next_url:next_url});
        htmlArr.push({ url: next_url, options: { headers: { 'User-Agent': user_agent ,'content-type':'application/json; charset='+charset } } });
    }
    htmlArr = batchFetch(htmlArr);
    var title = parseDomForHtml(htmlArr[0], title_rule);
    //log({title:title});
    putVar('title', title);
    htmlArr.forEach(x => {
        if(list_rule==''){
            var pic = parseDomForHtml(x, pic_rule)
            //log({pic:pic});
            pics.push(pic);
        }else {
            var list = parseDomForArray(x, list_rule)
        //log({list:list})
        for (var key in list) {
            var pic = parseDomForHtml(list[key], pic_rule)
            //log({pic:pic});
            pics.push(pic);
        }
        }
    });
    for (x in pics) {
        d.push({
            pic_url: pics[x],
            url: pics[x],
            col_type: "pic_3"
        });
    }
    putVar('pics', pics);
    d.unshift({
	  title:title + "("+pics.length+"p)",
	  col_type: 'text_center_1', extra: { lineVisible: false }
	});
    d.push({
        title: '下载全部图片',
        url: $(' 是否下载全部图片').confirm(_ => {
            var picsStr = getVar('pics')
            var titleStr = getVar('title');
            var jsonObj = JSON.parse(picsStr);//转换为json对象
            var pics = [];
            for (var i = 0; i < jsonObj.length; i++) {
                var s = jsonObj[i];
                log({ p: s });
                var file = s.split(".");
                var len = file.length
                var suffix = file[len - 1];
                if (!fileExist('hiker://files/download/' + titleStr + '/' + (i + 1) + '.' + suffix)) {
                    saveImage(s, 'hiker://files/download/' + titleStr + '/' + (i + 1) + '.' + suffix);
                }
            }

            return 'toast://下载成功';
        }),
        col_type: 'text_center_1', extra: { lineVisible: false }
    });

} catch (error) {
    log({ error: error });
}
res.data = d;
setHomeResult(res);

