var d = [];

/*--变量名别动--*/
const Color = "#19B89D";
var html = getResCode();
//标题
var title = pdfh(html, 'span&&Text');
var title_big = title; //显示的白色大字
//封面图 一定不要加Referer
var des_pic = pd(html, '.detail-main-cover&&img&&data-original');
//剧情简介

var des_desc = pdfh(html, '.detail-desc&&Text').replace(/详情|介绍|简介/, '').substring(0, 65);
//文字描述<br>换行
var des_text =  pdfh(html, '.detail-main-info-class&&Text') + '<br>' + pdfh(html, '.detail-list-title-3&&Text') + '<br>简介：' + des_desc;

//调用二级样式头部
require('http://hiker.nokia.press/hikerule/rulelist.json?id=2968');
//执行x5样式
	header(d, {
	大字: title_big,
	片名: title,
	图片: des_pic,
	描述: des_text,
})

function 解析() {
    var htmls = request(input);
    var list=parseDomForArray(htmls,"body&&#cp_img&&img");
    var pics = [];
    for (i in list){
        pics.push(parseDomForHtml(list[i],"img&&data-original")+ "@Referer=https://www.feixuemh.com/")};
    return "pics://" + pics.join("&&")
}

let lazy = $("").lazyRule(解析);
let downloadlazy = $.toString(解析);

var doList = []
var chapter_list = []
pdfa(html, 'body&&#chapter_indexes').forEach(x => {
    chapter_list = chapter_list.concat(pdfa(x, 'ul&&li'))
})
for (let i in chapter_list) {
    let title = pdfh(chapter_list[i], 'Text')
    let url = pd(chapter_list[i], 'a&&href')
    d.push({
        title: title,
        url: url + lazy,
        col_type: 'text_3'
    })

    doList.push({
        title: title, // 章节名字
        url: url, // 章节url
    });
}
//新增传输
var 传输数据 = {
    下载列表:doList,
    下载解析:downloadlazy,
    漫画名称:pdfh(html, 'span&&Text'),
    漫画的展示图:pdfh(html, '.detail-main-info&&img&&data-original'),
}
putVar('下载器',传输数据)
setResult(d);

