js:
var d = [];

var cookie = fetch('hiker://files/cache/javdbcookie.txt', {});
var ResCode = request(getUrl().replace('hiker://empty', 'https://javdb008.com'), {
    headers: {
        'Cookie': cookie
    }
});


var video_title = parseDomForHtml(ResCode, 'body&&.title&&Text');
var video_num = parseDomForHtml(ResCode, 'body&&.value&&Text').toLowerCase();
var video_pic = parseDomForHtml(ResCode, 'body&&.video-cover&&src');


try {
    var video_url = 'https:' + parseDomForHtml(ResCode, 'body&&source&&src');
    var video_desc = '点此查看视频预览';
    if (video_url == '') {
        var video_url = video_pic;
        var video_desc = '当前影片暂无视频预览';
    }
} catch (e) {
    var video_url = video_pic;
    var video_desc = '当前影片暂无视频预览';
}

d.push({
    title: video_title,
    desc: video_desc,
    pic_url: video_pic,
    url: video_url,
    col_type: 'pic_1_card'
});

var json1 = JSON.parse(fetch('https://jable.tv/videos/'+video_num+'/',{withStatusCode:true}));
if(json1.statusCode=='200'){
d.push({title:'jable(可播放)',
url:json1.body.match(/hlsUrl = '(.*?)';/)[1],
col_type:'text_2'
});}else{d.push({title:'jable(不可播放)',col_type:'text_2' });}

var json2 = JSON.parse(fetch('https://javmovs.com/'+video_num,{withStatusCode:true}));
if(json2.statusCode=='200'){
d.push({title:'javmovs(可播放)',
url:"hiker://page/javdb/child?",extra:JSON.stringify({key:json2.body}),col_type:'text_2'});}else{
d.push({title:'javmovs(不可播放)',col_type:'text_2' });}

d.push({
    col_type: 'line_blank'
});

var info_list = parseDomForArray(ResCode, 'body&&.movie-panel-info&&.panel-block');
d.push({
    title: '影片详情',
    col_type: 'text_center_1'
});
for (var i = 0; i < (info_list.length - 2); i++) {
    d.push({
        title: parseDomForHtml(info_list[i], '.panel-block&&Text'),
        url: 'hiker://search',
        col_type: 'text_1'
    });
}

d.push({
    col_type: 'line_blank'
});

var pic_list = parseDomForArray(ResCode, 'body&&.preview-images&&.tile-item');
if (pic_list != '') {
    d.push({
        title: '图片预览',
        col_type: 'text_center_1'
    });
    for (var i in pic_list) {
        d.push({
            pic_url: parseDomForHtml(pic_list[i], 'a&&href'),
            url: parseDomForHtml(pic_list[i], 'a&&href'),
            col_type: 'movie_3'
        });
    }
} else {
    d.push({
        title: '当前影片暂无图片预览',
        col_type: 'text_center_1'
    });
}


d.push({
    col_type: 'line_blank'
});

var down_list = parseDomForArray(ResCode, 'body&&#magnets-content&&.magnet-name');
if (down_list != '') {
    d.push({
        title: '下载链接',
        col_type: 'text_center_1'
    });
    for (var i in down_list) {
        d.push({
            title: parseDomForHtml(down_list[i], 'a&&href'),
            desc: parseDomForHtml(down_list[i], 'a&&Text'),
            url: parseDom(down_list[i], 'a&&href') + 'ignoreVideo=true',
            col_type: 'text_1'
        });
    }
} else {
    d.push({
        title: '当前影片暂无下载链接',
        col_type: 'text_center_1'
    });
}


setResult(d);

