var list = JSON.parse(html).data.datas;
//log(list[0]);

for (let j = 0; j < list.length; j++) {
    var desc = list[j].introduction;
    var img = list[j].screenshot;
    var url = list[j].profileRoom;
    var title = list[j].nick;
    items.push({
        title: title,
        pic_url: img,
        url: $(url).lazyRule((title, img) => {
            //å·æ¯›é¼ ç›´æ’­ä»£ç†https://github.com/JMSTV/jmstv.github.io
            //https://epg.112114.xyz/huya/
            //https://live.52sf.ga/huya/
            /*
            var ul = JSON.parse(request('https://epg.112114.xyz/huya/' + input, {
                redirect: false,
                withHeaders: true
            }));
            return ul.headers.location[0];
            */

            //let rid = input.split('/').pop();
            let html = fetch('https://m.huya.com/' + input, {
                headers: {
                    "User-Agent": MOBILE_UA
                }
            });

            let live_json = html.match(/window.HNF_GLOBAL_INIT = (.*?)<\/script>/)[1];
            let live = JSON.parse(live_json);
            let purl = JSON.stringify({
                name: title,
                urls: [getRealUrl(base64Decode(live.roomProfile.liveLineUrl)).replace('//', 'http://')],
                pic: img,
                guolv: "#ignoreImg=true#"
            });

            //log(purl);
            if (getMyVar('huyazb', '0') == '1') {
                return 'hiker://page/push?rule=XYQæ¨é€&pushurl=' + encodeURIComponent(purl.replace(/\&/g, 'ï¼†ï¼†'));
            } else {
                return purl
            }
            //ä»¥ä¸‹ä»£ç æ¥è‡ªTyrantGè™ç‰™ç›´æ’­
            function getRealUrl(live_url) {
                let [i, b] = live_url.split('?');
                let r = i.split('/').pop();
                let s = r.replace(/\.(flv|m3u8)/, '');
                let c_tmp = b.split('&').filter(n => n);
                // let c = []
                let n = {};
                let c_tmp2 = [];
                c_tmp.forEach((tmp, index) => {
                    if (index < 3) {
                        // c.push(tmp)
                        n[tmp.split('=')[0]] = tmp.split('=')[1]
                    } else {
                        c_tmp2.push(tmp)
                    }
                });
                let tmp2 = c_tmp2.join('&');
                n[tmp2.split('=')[0]] = tmp2.split('=')[1];

                let fm = decodeURIComponent(n.fm).split('&')[0];
                let u = base64Decode(fm);
                let p = u.split('_')[0];
                let f = new Date().getTime() + '0000';
                let ll = n.wsTime;
                let t = '0';
                let h = [p, t, s, f, ll].join('_');
                let m = md5(h);
                return (i + "?wsSecret=" + m + "&wsTime=" + ll + "&u=" + t + "&seqid=" + f + "&" + c_tmp2.pop()).replace('hls', 'flv').replace('m3u8', 'flv');
            }

        }, title, img),
        desc: desc
    });
}

setHomeResult({
    data: items
});

