var lazy = $("").lazyRule(() => {
    var player = JSON.parse(request(input).match(/r player_.*?=(.*?)</)[1]);
    var jsurl = unescape(base64Decode(player.url));
    var from = player.from;
    log('jsurl=' + jsurl + '\n' + 'parse=' + from);
    if (jsurl.match(/youku|mgtv|ixigua|qq\.com|qiyi|migu|bili|sohu|pptv|le/)) {
        require('https://gitea.com/AI957/Hiker/raw/m/v/Route.js');
        return aytmParse(jsurl);
    } else if (from.match(/m3u8/)) {
        return jsurl;
    } else if(jsurl.match(/renrenmi/)){
        var urll = request("https://jiexi.moeamv.com/analysis.php?v="+jsurl).match(/urls = "(.*?)"/)[1];
        writeFile('hiker://files/cache/video.m3u8', fetch(urll, {}));
