let getTimeState = () => {
    let timeNow = new Date();
    let hours = timeNow.getHours();
    let text = ``;
    if (hours >= 7 && hours <= 18) {
        text = `☀️`;
    } else {
        text = '🌙';
    }
    return text;
};

function getWeekData(html, text, zero) {
    var data = []
    var last = 7
    var istart = 1
    var ilast = 6
    if (zero) {
        last = last - 1
        istart = istart - 1
        ilast = ilast - 1
    }
    var s = pdfa(html, text.replace('**', last))
    data.push(s)

    for (let i = istart; i <= ilast; i++) {
        var h = pdfa(html, text.replace('**', i))
        data.push(h)
    }
    //log(data.length)
    return data
}


