var r = $.require("Slide?rule=" + MY_RULE.title)


function slideFold(d, html, obj, fold, dc) {
    var f = 0
    var dcover = false;
    if (dc != undefined && dc == true) {
        dcover = true
    }
    if (fold != undefined) {
        f = fold
    }
    var name = obj.name
    var title = obj.title
    var img = obj.img
    var url = obj.url
    var seles = obj.seles

    var config = obj.config ? obj.config : {
        x5h:190,
        newW: false,
        mark: "#immersiveTheme#"
    }
    if(Object.keys(storage0.getItem('sconfig',{})).length!=0){
        config=storage0.getItem('sconfig')
    }

    if (name != undefined) {

        let dm = 0;
        for (let i = 0; i < d.length; i++) {
            var b = (new RegExp(`^${name}$`).test(d[i].title))
            if (b) {
                dm = i;
                break;
            }
        }
        url = d[dm].url

    }

    d.push({
        title: title,
        col_type: 'avatar',
        img: img,
        url: url,
        extra: {
            longClick: [{
                title: '清空数据',
                js: $.toString(() => {
                    clearItem("sildedata");
                    refreshPage()
                    return 'toast://完成'
                })
            }]
        }
    })

    if (f == 0) {
        selects = seles;
        var item = selects.item;
        var list = selects.list;
        var imgx = selects.img;
        var title = selects.title;
        var desc = selects.desc;
        var url = selects.url;
        var col_type = selects.col_type;
        var dimg = selects.dimg;

        list = eval(list);
        var sildedata = storage0.getItem("sildedata", []);
        var setlist = sildedata.map(x => x.url);
        var getlist = list.map((item) => {
            return eval(url)
        });
        var equal = getDifferent(getlist, setlist).length == 0 ? true : false;
        if (equal) {
            var items = sildedata;
        } else {
            var items = list.map((item) => {
                var obj = {};
                var urlx = eval(url);
                obj.title = eval(title);
                obj.url = urlx;
                obj.desc = eval(desc);
                if (dcover) {
                    obj.cover = pdfh(request(urlx), dimg)
                } else {
                    obj.cover = eval(imgx);
                }
                return obj;
            })
            storage0.setItem("sildedata", items);
        }
        var data = Object.assign({}, {
            data: items
        }, {
            config:config
        })
        r.show(data, d)
    }
}

function tablist(d, html, obj) {

    var tlist = obj.tlist
    var tobj = obj.tobj
    var key = obj.tkey
    var title = obj.title
    var img = obj.img
    var url = obj.url
    var seles = obj.seles
    var col=obj.col

    d.push({
        title: title,
        col_type: 'avatar',
        img: img,
        url: url
    })

    var fi = storage0.getItem('tfold')[key]

    if (fi == 1) {
        return
    }

    var v = tobj[key]
    seles.list = obj.nlist.replace('**', v)

    if(col!=undefined){
        col=col
    }else if([3,6,9].includes(tlist.length)){
        col='text_3'
    }else if([5,10].includes(tlist.length)){
        col='text_5'
    }else if([4,8].includes(tlist.length)){
        col='text_4'
    }else if([2].includes(tlist.length)){
        col='text_2'
    }else{
        col='flex_button'
    }

    if (tlist)
        tlist.map((x, i) => {
            var title = i == v ? `““””<b><font color="#1E90FF">${x}</font></b>` : x

            d.push({
                title: title,
                col_type: col,
                url: $('').lazyRule((i, t, k) => {
                    t[k] = i
                    storage0.putMyVar('tobj', t)
                    refreshPage(false)
                    return 'hiker://empty'
                }, i, tobj, key)
            })
        })

    appedData(d, html, seles)
}


