var d = [];
var html = getResCode();

var lazy = $('').lazyRule(() => {

    function toUrl(url) {
         //log("播放地址-->" + url);
        if (url.indexOf('bilivideo') != -1) {
            return url + ';{Referer@https://www.bilibili.com&&User-Agent@Mozilla/5.0}';
        } else if (url.indexOf('titan.mgtv.com') != -1) {
            return url + '#isVideo=true#' + ';{Referer@www.mgtv.com&&User-Agent@Mozilla/5.0}';
        } else if (url.indexOf('com/gzc') != -1) {
            return url + ';{Referer@https://www.laodiys.com/}';
        } else {
            return url;
        }

    }
    var playUrl = "";
    try {
        var html = request(input, {});
        var player_data = JSON.parse(html.match(/r player_.*?=(.*?)</)[1]);

        var fro = player_data.from;
        playUrl = player_data.url;

        if (player_data.encrypt == '1') {
            playUrl = unescape(player_data.url);
        } else if (player_data.encrypt == '2') {
            playUrl = unescape(base64Decode(player_data.url));
        };
        //如果播放地址存在m3u8等扩展名直接返回
        if (playUrl.match(/\.m3u8|\.mp4|obj\/tos|\.flv/) && !playUrl.match(/html|m3u8\.tv|\&next|ac\=dm|\=http/)) {
            return toUrl(playUrl);
        }
        //获取下网站配置文件的解析接口
        var jvs = parseDom(html, "body&&.embed-responsive&&script,1&&src");
        //log(jvs);

        eval(request(jvs, {}));
        var jiek = '';
        if (MacPlayerConfig.player_list[fro].ps == "1") {
            jiek = MacPlayerConfig.player_list[fro].parse == '' ? MacPlayerConfig.parse : MacPlayerConfig.player_list[fro].parse;
        } else {
            var jxkJSPath = "https://www.laodiys.com/static/player/" + fro + ".js";
            //log("jxkJSPath-->" + jxkJSPath);
            var jiekHtml = request(jxkJSPath, {});
            //log("jiekHtml-->" + jiekHtml);
            jiek = jiekHtml.match(/src="(.*?)'/)[1].replace("ifr.php", "ifrty.php");
            if (jiek != '') {
                if (jiek.substring(0, 4) == 'http') {
                    jiek = jiek;
                } else if (jiek.substring(0, 2) == '\/\/') {
                    jiek = 'https:' + jiek;
                } else {
                    jiek = "https://www.laodiys.com" + jiek
                }
            }
        }
        var jiexi = jiek + playUrl;
        //log("jiexi-->" + jiexi);
        var playerHtml = request(jiexi, {
            headers: {
                "Referer": "https://www.laodiys.com/"
            }
        });

        if (fro == "leduo") {
            var tempUrl = base64Decode(playerHtml.match(/url1='(.*?)'/)[1].replace(/RGlkLnBocD9WQ9WED92aWQ9WE1NVFU12awD92aWQ9WE1NVFU1TWQ9WE1NVFU1TWpaNrdt05/, ""));
			playUrl = decodeURIComponent(tempUrl);
           // log("leduo-->" + playUrl);
        }      
      else if (fro == "fanqie") {
            playUrl = playerHtml.match(/url": "(.*?)"/)[1];
           // log("fanqie-->" + playUrl);
        }
        //log("playurl-->" + playUrl);

        if (playUrl.match(/\.m3u8|\.mp4|obj\/tos|\.flv/) && !playUrl.match(/html|m3u8\.tv|\&next|ac\=dm|\=http/)) {
            return toUrl(playUrl);
        } else {
            showLoading("正在进行检索，请稍候...");
            var video = 'x5Rule://' + input + '@' + $.toString(() => {
                //fba.log(fba.getUrls());
                var urls = _getUrls();
                if (urls.length > 0) {
                    for (var i in urls) {
                        if (urls[i].match(/\.m3u8|\.mp4|obj\/tos|\.flv/) && !urls[i].match(/html|m3u8\.tv|\&next|ac\=dm|\=http/)) {
                            //fy_bridge_app.log(urls[i])
                            if (urls[i].indexOf('bilivideo') != -1) {
                                return urls[i] + ';{Referer@https://www.bilibili.com&&User-Agent@Mozilla/5.0}';
                            } else if (urls[i].indexOf('titan.mgtv.com') != -1) {
                                return urls[i] + '#isVideo=true#' + ';{Referer@www.mgtv.com&&User-Agent@Mozilla/5.0}';
                            } else {
                                return urls[i]
                            }
                        }
                    }
                } else {
                    fba.hideLoading();
                    return 'toast://嗅探失败，可能链接无法访问。'
                }
            });
            return video
        }
    } catch (e) {
        //setError(e);
        return 'toast://嗅探异常，可能链接无法访问。'
    }
});

