function renderLiveData(url, filter, isText) {
    let net = fetchCache(url, {
        headers: {
            Referer: getReferer(url)
        }
    });
    const m3u = net,
        mode = m3u.indexOf('#EXTM3U') == 0,
        re = mode ? /(.*),(.*)\s*?((?:http|rtmp)[^#\s]*)/g : /()(.*),(#genre#|(?:http|rtmp)[^#\s]*)/g;
    let items = {},
        group = '未分组';
    for (let i = re.exec(m3u); i != null; i = re.exec(m3u)) {
        if (mode) {
            group = (/group-title="(.*?)"/.exec(i[1]) || [null, ''])[1];
        } else if (i[3] == '#genre#') {
            group = i[2];
            continue;
        }
        let title = i[2],
            url = i[3].trim();
        if (url.indexOf(".php") > 0 || url.includes("@lazyRule") || url.includes(";")) {
            title = title + "###" + new Date().getTime() + md5(url);
            //log(j);
        } else {
            //url = url + "#isVideo=true#";
        }
        if (filter != null && !title.includes(filter)) {
            continue
        }
        if (items[group]) {
            if (items[group][title]) {
                items[group][title] = items[group][title].concat([url]);
            } else {
                items[group][title] = [url];
            }
        } else {
            items[group] = {};
            items[group][title] = [url];
        };
