function getData(datan) {
    let arr = [];
    let datat = '';
    arr = xpathArray(html, "//span[contains(text(),'" + datan + "')]/following-sibling::*//text()");
    if (arr.length == 0) {
        arr = xpathArray(html, "//span[contains(text(),'" + datan + "')]/parent::*/text()");
    }
    arr = arr.filter(x => x != 0)

    //log(arr)
    switch (datan) {
        case '主演':
            if (arr.length != 0) {
                datat = datan + '：' + arr.slice(0, 5).join('、');
            }
            break;

        case '剧情':
            datat = datan + '：' + arr.slice(1, 2).join('、');
            break;
        default:

            datat = datan + '：' + arr.join('、');
            break;
    }
    datat = datat.replace(/&middot;/g, '·')
    return datat;
}




