let getTimeState = () => {
    let timeNow = new Date();
    let hours = timeNow.getHours();
    let text = ``;
    if (hours >= 7 && hours <= 18) {
        text = `☀️`;
    } else {
        text = '🌙';
    }
    return text;
};


function getWeekData(html, text) {
    try{
    var data = []
    var s = pdfa(html, text.replace('**', 6))
    data.push(s)
    for (let i = 0; i <= 5; i++) {
        var h = pdfa(html, text.replace('**', i))
        data.push(h)

    }
    //log(data.length)
    return data
    }catch{}
}



function weekAppend(d, obj) {
    //定义常量和方法
    var 礼拜 = ["日", "一", "二", "三", "四", "五", "六"];

    let 星期 = (x, y) => {
        let day = x.getDay();
        return y[day];
    }
    var 日期 = new Date();

    //传递变量
    var data = obj.data
    var title = obj.title
    var daynight = obj.dn
    var img = obj.img
    var url = obj.url
    var show = obj.show
    var wcolor = obj.wcolor
    var wstr = obj.wstr

    d.push({
        title: title,
        col_type: 'avatar',
        img: img,
        url: url
    })

    if (show == '展开') {
        var day = new Date().getDay()
        var week = getMyVar('week', day + '')
        we = 礼拜[week]
        for (var i = 0; i < 礼拜.length; i++) {
            d.push({
                title: week == i ? '““””' + daynight + `<font color="${wcolor}"><b>` + wstr + we + '</b></font>' : 礼拜[i],
