                    putMyVar("week", Number(it));
                    refreshPage(true);
                    return 'hiker://empty'
                }, i),
                col_type: "scroll_button",
                extra: {
                    id: 'week' + i,
                }
            })
        }

        d.push({
            col_type: "line",
            pic: "",
            extra: {
                id: "good"
            }
        })


        data[week].forEach((x) => {
            var t = pdfh(x, 'a,0&&Text')
            var s = pdfh(x, 'a,1&&Text')
            var title=cutstr(t,40)

            //links.push(pd(x, 'a&&href') + '#immersiveTheme#')
            d.push({
                title: `““””<small>[<font color="#999">${s}</font>]${title}</small>`,
                url: pd(x, 'a&&href') + '#immersiveTheme#',
                col_type: 'text_1',
                extra: {
                    title:t
                }
            })
        })
    }
}

