function jumpMore(d, html, obj) {

    var name = obj.name
    var title = obj.title
    var img = obj.img
    var url = obj.url
    var seles = obj.seles
    seles.list = obj.nlist
    seles.title = obj.ntitle

    if (name != undefined) {
        let dm = 0;
        for (let i = 0; i < d.length; i++) {
            var b = (new RegExp(`^${name}$`).test(d[i].title))
            if (b) {
                dm = i;
                break;
            }
        }
        url = d[dm].url
    }

    d.push({
        title: title,
        col_type: 'avatar',
        img: img,
        url: url
    })
    appedData(d, html, seles)
}

function rankAdd(d, html, obj) {
