    d.push({
        title: title,
        col_type: 'avatar',
        img: img,
        url: url
    })

    var list = eval(seles.list)

    list.map((item, i) => {
        i = i + 1
        var title = i + '.' + eval(seles.title)
        var hit=``

        switch (i) {
            case 1:
                title = `““””<small><font color="#FF0000">${title}</font>${hit}</small>`
                break;
            case 2:
