            })
        }
        data.push({
            title: "ËÆæÁΩÆ",
            col_type: "scroll_button",
            url: $("").lazyRule(() => {
                putVar("set", '#setting');
                refreshPage(false);
                return "hiker://empty"
            })
        })
        data.push({
            col_type: 'line'
        })
        for (source of config.data) {
            if (source.name == select) {
                try {
                    let items = $.require('hiker://page/renderData').renderLiveData(source.url);
                    let groups = Object.keys(items);
                    groups.push(groups.splice(groups.indexOf(0), 1)[0]);
                    let selectGroup = getMyVar('selectGroup', groups[0]);
                    if (selectGroup == "") selectGroup = groups[0];
                    groups.forEach((group) => {
                        if (group) {
                            data.push({
                                title: group == selectGroup ? "‚Äú‚Äú‚Äù‚Äù<span style='color: #19B89D'>" + group + "</span>" : group,
                                url: $('#noLoading#').lazyRule((group) => {
                                    putMyVar('selectGroup', group);
                                    refreshPage();
                                    return 'hiker://empty'
                                }, group),
                                col_type: 'flex_button',
                                extra: {
                                    lineVisible: false
                                }
                            })
                        }
                    })
                    for (let title in items[selectGroup]) {
                        data.push({
                            title: title,
                            url: JSON.stringify({
                                urls: items[selectGroup][title]
                            }),
col_type: 'icon_2_round',
                    img: "https://lanmeiguojiang.com/tubiao/more/228.png"
                        })
                    }
                } catch (e) {
                    data.push({
                        title: "Êï∞ÊçÆÂä†ËΩΩÂ§±Ë¥•",
                        desc: JSON.stringify(e),
                        col_type: "text_1"
                    })
                }
                break;
            }
        }
    }
}
setResult(data);

