                print('ÂêäÊØõËßÜÈ¢ë,Ëá™Âä®ËøáÊª§:'+videoUrl);
                return true
            }
        }
        print('ÈùûÂêäÊØõËßÜÈ¢ë,ÂèØÊí≠Êîæ:'+videoUrl);
        return false
    }

    function isDm(ret) {
        if (!ret) {
            return true
        }
        return hasDm(ret.split('#')[0].split(';')[0], dmUrls)
    }
    return isDm(videoUrl)
}

