        let [_, path, flag, params] = el.url.match(/^hiker:\/\/page\/(.+?)(#.*?)?(?:\?(.*))?$/),
            subPage = MY_RULE.pageList.find((v) => v.path == path),
            subUrl = (params || '').split('&').find((v) => v.startsWith('url='))
        el.url = (subUrl ? subUrl.slice(4).replace(/ï¼Ÿï¼Ÿ/g, '?').replace(/ï¼†ï¼†/g, '&') : (el.extra || {}).url) || 'hiker://empty' + flag + '?' + (params || '')
        NOW_RULE.detail_find_rule = subPage.rule
        NOW_RULE.detail_col_type = subPage.col_type
    } else if (el.url.includes('@lazyRule=')) {
        let reIndex = el.url.indexOf('.js:')
        if (reIndex >= 0) el.url = el.url.slice(0, reIndex) + '.js:' + hijackLazyRule + ';' + el.url.slice(reIndex + 4)
        return el
    } else if (isIndex) {
        if (!MY_RULE.detail_find_rule || /hiker:\/\/(?!empty.+$)/.test(el.url)) return el
    } else {
        return el
    }
    el.extra = Object.assign({}, el.extra, {
        url: genExUrl(el.url),
        RULE: base64Encode(JSON.stringify(NOW_RULE)),
        pageTitle: isIndex && el.title
    })
    el.url = 'hiker://page/frameLoad?rule=' + QING_TITLE
    return el
}
$.exports = {
    runCode: runCode,
